import { ZodError } from 'zod';
import { Response, Request } from 'express';
import MongoControllers, {
  RequestWithBody,
  ResponseError,
} from './MongoControllers';
import CarServices from '../services/CarServices';
import carSchema, { Car as ICar } from '../interfaces/CarInterface';

class CarControllers extends MongoControllers<ICar> {
  constructor(protected service = new CarServices()) {
    super(service, '/cars');
  }

  create = async (
    req: RequestWithBody<ICar>,
    res: Response<ICar | ResponseError>,
  ): Promise<typeof res> => {
    try {
      const { body } = req;

      carSchema.parse(body);

      const car = await this.service.create(body);

      return res.status(201).json(car);
    } catch (err) {
      if (err instanceof ZodError) {
        return res.status(400).json({ error: err.flatten().fieldErrors });
      }

      return res.status(500).json({ error: this.errors.internal });
    }
  };

  readOne = async (
    req: Request<{ id: string }>,
    res: Response<ICar | ResponseError>,
  ): Promise<typeof res> => {
    try {
      const { id } = req.params;

      if (id.length < 24) {
        return res.status(400).json({ error: this.errors.idMust });
      }

      const car = await this.service.readOne(id);

      return car
        ? res.status(200).json(car)
        : res.status(404).json({ error: this.errors.notFound });
    } catch (err) {
      return res.status(500).json({ error: this.errors.internal });
    }
  };

  update = async (
    req: Request<{ id: string }>,
    res: Response<ICar | ResponseError>,
  ): Promise<typeof res> => {
    try {
      const { params: { id }, body } = req;

      if (id.length < 24) {
        return res.status(400).json({ error: this.errors.idMust });
      }

      carSchema.parse(body);

      const car = await this.service.update(id, body);

      return car ? res.status(200).json(car)
        : res.status(404).json({ error: this.errors.notFound });
    } catch (err) {
      if (err instanceof ZodError) {
        return res.status(400).json({ error: err.flatten().fieldErrors });
      }

      return res.status(500).json({ error: this.errors.internal });
    }
  };

  delete = async (
    req: Request<{ id: string }>,
    res: Response<object | ResponseError>,
  ): Promise<typeof res> => {
    try {
      const { id } = req.params;

      if (id.length < 24) {
        return res.status(400).json({ error: this.errors.idMust });
      }

      const car = await this.service.delete(id);

      return car
        ? res.status(204).json({})
        : res.status(404).json({ error: this.errors.notFound });
    } catch (error) {
      return res.status(500).json({ error: this.errors.internal });
    }
  };
}

export default CarControllers;

import { Request, Response } from 'express';
import ControllerErrors from '../enum/erros';
import MongoServices from '../services/MongoServices';

export type ResponseError = {
  error: unknown;
};

export interface RequestWithBody<T> extends Request {
  body: T;
}

abstract class MongoControllers<T> {
  protected errors = ControllerErrors;

  constructor(protected service: MongoServices<T>, public route: string) {}

  abstract create(
    req: RequestWithBody<T>,
    res: Response<T | ResponseError>,
  ): Promise<typeof res>;

  read = async (
    _req: Request,
    res: Response<T[] | ResponseError>,
  ): Promise<typeof res> => {
    try {
      const cars = await this.service.read();

      return res.json(cars);
    } catch (err) {
      return res.status(500).json({ error: this.errors.internal });
    }
  };

  abstract readOne(
    req: Request<{ id: string }>,
    res: Response<T | ResponseError>,
  ): Promise<typeof res>;

  abstract update(
    req: Request<{ id: string }>,
    res: Response<T | ResponseError>,
  ): Promise<typeof res>;

  abstract delete(
    req: Request<{ id: string }>,
    res: Response<T | ResponseError>,
  ): Promise<typeof res>;
}

export default MongoControllers;

import { Car as ICar } from '../interfaces/CarInterface';
import CarModels from '../models/CarModels';
import MongoServices from './MongoServices';

class CarServices extends MongoServices<ICar> {
  constructor(public model = new CarModels()) {
    super(model);
  }
}

export default CarServices;

import { Document, Schema, model as MModel } from 'mongoose';
import { Car as ICar } from '../interfaces/CarInterface';
import MongoModels from './MongoModels';

interface CarDocument extends ICar, Document {}

const carSchema = new Schema<CarDocument>(
  {
    model: { type: String, required: true },
    year: { type: Number, required: true },
    color: { type: String, required: true },
    status: Boolean,
    buyValue: { type: Number, required: true },
    doorsQty: { type: Number, required: true },
    seatsQty: { type: Number, required: true },
  },
  { versionKey: false },
);

class CarModels extends MongoModels<ICar> {
  constructor(public model = MModel('Cars', carSchema)) {
    super(model);
  }
}

export default CarModels;

import { Model as MModel, Document } from 'mongoose';
import { Model as IModel } from '../interfaces/ModelInterface';

abstract class MongoModels<T> implements IModel<T> {
  constructor(protected model: MModel<T & Document>) {}

  create = async (obj: T): Promise<T> => this.model.create({ ...obj });

  read = async (): Promise<T[]> => this.model.find();

  readOne = async (id: string): Promise<T | null> =>
    this.model.findOne({ _id: id });

  update = async (id: string, obj: T): Promise<T | null> =>
    this.model.findByIdAndUpdate({ _id: id }, { ...obj }, { new: true });

  delete = async (id: string): Promise<T | null> =>
    this.model.findByIdAndDelete({ _id: id });
}

export default MongoModels;

import MongoModels from '../models/MongoModels';

abstract class MongoServices<T> {
  constructor(protected model: MongoModels<T>) {}

  public async create(obj: T): Promise<T> {
    const car = await this.model.create(obj);
    return car;
  }

  public async read(): Promise<T[]> {
    const cars = await this.model.read();
    return cars;
  }

  public async readOne(id: string): Promise<T | null> {
    const car = await this.model.readOne(id);
    return car;
  }

  public async update(id: string, obj: T): Promise<T | null> {
    const car = await this.model.update(id, obj);
    return car;
  }

  public async delete(id: string): Promise<T | null> {
    const car = await this.model.delete(id);
    return car;
  }
}
export default MongoServices;

